import ChatBot from 'react-simple-chatbot';
import React from "react";

const Chat = () => {
    const steps = [
        {
            id: 'question-prompt',
            message: 'Ask a question related to religious studies.',
            trigger: 'question'
        },
        {
            id: 'question',
            user: true,
            trigger:'ask',
        },
        {
            id: 'ask',
            component: <Ask />,
            waitAction:true,
            trigger:'response',
        },
        {
            id: 'response',
            message: '{previousValue}',
            trigger: 'question',
        },
    ];

    return (<div>
        <ChatBot bubbleStyle={{"whiteSpace":"pre-line",
                              "fontFamily": "sans-serif",
                              "fontSize":16}}
                bubbleOptionStyle={{"fontFamily": "sans-serif",
                                    "fontSize":16}}
                customStyle={{"fontFamily": "sans-serif",
                                "fontSize":16}}
                headerTitle="RELS GPT"
                width="800px"
                steps={steps}/>
    </div>);
}

export default Chat;
