import ChatBot from 'react-chatbotify';
import React from "react";

const ASK_ENDPOINT = "http://127.0.0.1:5000/ask?"

const Chat = () => {
    async function askQuestion(q) {
        try {
            const response = await fetch(ASK_ENDPOINT + new URLSearchParams({
                question: q
            }));
            const data = await response.json();
            console.log(data)
            return data.body;
        } catch (error) {
            return "Something went wrong";
        }
    };
    const flow={
        start: {
            message: "Do you have any questions about religious studies?",
            path: "loop"
        },
        loop: {
            message: async (params) => {
                const result = await askQuestion(params.userInput);
                return result;
            },
            path: "loop",
        }
    };

    return (
        <ChatBot options={{theme: {embedded: true}}} flow={flow} />
    );
}


export default Chat;
